import React from 'react';
import { twMerge } from 'tailwind-merge';

function Button({ children, className, ...props }) {
  const baseButtonClasses = `
    px-4
    py-2
    rounded-md
    font-semibold
    text-white
    bg-pink-500
    hover:bg-pink-700
    focus:outline-none
    focus:ring-2
    focus:ring-pink-300
    transition-colors
    duration-200
  `;

  const mergedClasses = twMerge(baseButtonClasses, className);

  return (
    <button className={mergedClasses} {...props}>
      {children}
    </button>
  );
}

export default Button;

